import React, { useState } from 'react';
import { Send, HelpCircle, CheckCircle, ArrowRight, Loader2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const [currentState, setCurrentState] = useState('initial'); // 'initial', 'questions', 'answer', 'loading'
  const [userPrompt, setUserPrompt] = useState('');
  const [clarifyingQuestions, setClarifyingQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [finalAnswer, setFinalAnswer] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Smart detection logic
  const needsClarification = (prompt) => {
    const lowerPrompt = prompt.toLowerCase();

    // Patterns that need clarification
    const clarificationPatterns = [
      /should i/i,
      /help me decide/i,
      /what should i do/i,
      /advice/i,
      /recommend/i,
      /choose/i,
      /better/i,
      /best/i,
      /worth it/i,
      /good idea/i,
      /bad idea/i,
      /pros and cons/i,
      /compare/i,
      /which/i,
      /what do you think/i,
      /opinion/i
    ];

    // Patterns that don't need clarification (factual questions)
    const factualPatterns = [
      /^what is/i,
      /^how to/i,
      /^define/i,
      /^explain/i,
      /^tell me about/i,
      /^what are/i,
      /^how does/i,
      /^when did/i,
      /^where is/i,
      /^who is/i,
      /^why does/i,
      /^calculate/i,
      /^convert/i,
      /^formula/i,
      /^definition/i,
      /^meaning/i
    ];

    // Check if it's a factual question first
    const isFactual = factualPatterns.some(pattern => pattern.test(prompt));
    if (isFactual) return false;

    // Check if it needs clarification
    return clarificationPatterns.some(pattern => pattern.test(prompt));
  };

  const generateSampleQuestions = (prompt) => {
    const lowerPrompt = prompt.toLowerCase();

    if (lowerPrompt.includes('move') || lowerPrompt.includes('relocate')) {
      return [
        "What's your current location?",
        "What's your budget range for housing?",
        "What are your main priorities (career, family, lifestyle)?"
      ];
    }

    if (lowerPrompt.includes('career') || lowerPrompt.includes('job')) {
      return [
        "What's your current field or industry?",
        "What's your experience level?",
        "What are your main career goals?"
      ];
    }

    if (lowerPrompt.includes('buy') || lowerPrompt.includes('purchase')) {
      return [
        "What's your budget range?",
        "What are your main requirements?",
        "What's your timeline for this purchase?"
      ];
    }

    if (lowerPrompt.includes('relationship') || lowerPrompt.includes('dating')) {
      return [
        "What's your current relationship status?",
        "What are you looking for in a relationship?",
        "What challenges are you facing?"
      ];
    }

    if (lowerPrompt.includes('health') || lowerPrompt.includes('medical')) {
      return [
        "What's your current health situation?",
        "Are you taking any medications?",
        "What are your main health concerns?"
      ];
    }

    // Default questions
    return [
      "Can you provide more context about your situation?",
      "What are your main priorities or constraints?",
      "What specific outcome are you hoping for?"
    ];
  };

  const handleSubmitPrompt = async () => {
    if (!userPrompt.trim()) return;

    setIsLoading(true);
    setCurrentState('loading');

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      if (needsClarification(userPrompt)) {
        const questions = generateSampleQuestions(userPrompt);
        setClarifyingQuestions(questions);
        setCurrentState('questions');
      } else {
        // Skip to answer for factual questions
        setCurrentState('answer');
        setFinalAnswer("This appears to be a factual question. I'll provide a direct answer based on available information.");
      }
    } catch (error) {
      console.error('Error checking clarification:', error);
      setCurrentState('initial');
    } finally {
      setIsLoading(false);
    }
  };

  const handleAnswerQuestions = async () => {
    setIsLoading(true);
    setCurrentState('loading');

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      setCurrentState('answer');
      setFinalAnswer("Based on your responses, here's a personalized answer to your question. [This would be generated by the LLM API with your clarifications.]");
    } catch (error) {
      console.error('Error generating answer:', error);
      setCurrentState('questions');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSkipQuestions = async () => {
    setIsLoading(true);
    setCurrentState('loading');

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      setCurrentState('answer');
      setFinalAnswer("Here's a general answer to your question. [This would be generated by the LLM API without clarifications.]");
    } catch (error) {
      console.error('Error generating answer:', error);
      setCurrentState('questions');
    } finally {
      setIsLoading(false);
    }
  };

  const handleNewQuestion = () => {
    setCurrentState('initial');
    setUserPrompt('');
    setClarifyingQuestions([]);
    setAnswers({});
    setFinalAnswer('');
  };

  const handleAnswerChange = (questionIndex, value) => {
    setAnswers(prev => ({
      ...prev,
      [questionIndex]: value
    }));
  };

  const renderInitialState = () => (
    <div className="text-center animate-fade-in">
      <div className="mb-8">
        <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full mb-4">
          <HelpCircle className="w-8 h-8 text-white" />
        </div>
        <h1 className="text-3xl font-bold text-foreground mb-2">Intent Clarifier</h1>
        <p className="text-muted-foreground">Get better responses by providing more context</p>
      </div>

      <div className="space-y-4">
        <Textarea
          value={userPrompt}
          onChange={(e) => setUserPrompt(e.target.value)}
          placeholder="Ask me anything... (e.g., 'Should I move to New York?')"
          className="min-h-[100px] resize-none"
        />

        <Button
          onClick={handleSubmitPrompt}
          disabled={!userPrompt.trim() || isLoading}
          className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700"
          size="lg"
        >
          {isLoading ? (
            <Loader2 className="w-5 h-5 animate-spin" />
          ) : (
            <>
              <Send className="w-5 h-5 mr-2" />
              Get Clarified Answer
            </>
          )}
        </Button>
      </div>
    </div>
  );

  const renderQuestionsState = () => (
    <div className="animate-fade-in">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-foreground mb-2">Let's clarify a few things</h2>
        <p className="text-muted-foreground">Answer these questions to get a more personalized response</p>
      </div>

      <div className="space-y-6">
        {clarifyingQuestions.map((question, index) => (
          <div key={index} className="animate-slide-up" style={{ animationDelay: `${index * 0.1}s` }}>
            <label className="block text-sm font-medium text-foreground mb-2">
              {question}
            </label>
            <Textarea
              value={answers[index] || ''}
              onChange={(e) => handleAnswerChange(index, e.target.value)}
              placeholder="Your answer..."
              className="min-h-[60px] resize-none"
            />
          </div>
        ))}

        <div className="flex flex-col sm:flex-row gap-3 pt-4">
          <Button
            onClick={handleAnswerQuestions}
            className="flex-1 bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700"
            size="lg"
          >
            <CheckCircle className="w-5 h-5 mr-2" />
            Continue with Answers
          </Button>

          <Button
            onClick={handleSkipQuestions}
            variant="outline"
            className="flex-1"
            size="lg"
          >
            Skip Questions
          </Button>
        </div>
      </div>
    </div>
  );

  const renderAnswerState = () => (
    <div className="animate-fade-in">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-foreground mb-2">Your Answer</h2>
        <p className="text-muted-foreground">Based on your question and clarifications</p>
      </div>

      <Card className="mb-6">
        <CardContent className="pt-6">
          <div className="prose max-w-none">
            <p className="text-foreground leading-relaxed whitespace-pre-wrap">
              {finalAnswer}
            </p>
          </div>
        </CardContent>
      </Card>

      <Button
        onClick={handleNewQuestion}
        className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700"
        size="lg"
      >
        <ArrowRight className="w-5 h-5 mr-2" />
        Ask Another Question
      </Button>
    </div>
  );

  const renderLoadingState = () => (
    <div className="text-center animate-fade-in">
      <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full mb-4">
        <Loader2 className="w-8 h-8 text-white animate-spin" />
      </div>
      <h2 className="text-xl font-semibold text-foreground mb-2">Analyzing your question...</h2>
      <p className="text-muted-foreground">This may take a few moments</p>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 dark:from-slate-900 dark:via-slate-800 dark:to-slate-900">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          <Card className="shadow-xl">
            <CardHeader>
              <CardTitle className="text-center">Intent Clarifier</CardTitle>
              <CardDescription className="text-center">
                Get better responses by providing more context
              </CardDescription>
            </CardHeader>
            <CardContent>
              {currentState === 'initial' && renderInitialState()}
              {currentState === 'questions' && renderQuestionsState()}
              {currentState === 'answer' && renderAnswerState()}
              {currentState === 'loading' && renderLoadingState()}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default App;
