import React from 'react';

const AnimatedGradient = () => {
  return (
    <div className="absolute inset-0 overflow-hidden rounded-2xl">
      {/* Base gradient layer */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 dark:from-slate-800 dark:via-slate-700 dark:to-slate-800"></div>

      {/* Animated gradient layers */}
      <div className="absolute inset-0">
        {/* Layer 1 - Blue to Purple */}
        <div 
          className="absolute inset-0 opacity-60 animate-pulse"
          style={{
            background: 'linear-gradient(45deg, #3b82f6, #8b5cf6, #06b6d4)',
            backgroundSize: '400% 400%',
            animation: 'gradientShift 8s ease-in-out infinite'
          }}
        ></div>

        {/* Layer 2 - Pink to Orange */}
        <div 
          className="absolute inset-0 opacity-40 animate-pulse"
          style={{
            background: 'linear-gradient(-45deg, #ec4899, #f97316, #eab308)',
            backgroundSize: '400% 400%',
            animation: 'gradientShift 12s ease-in-out infinite reverse'
          }}
        ></div>

        {/* Layer 3 - Green to Blue */}
        <div 
          className="absolute inset-0 opacity-30 animate-pulse"
          style={{
            background: 'linear-gradient(90deg, #10b981, #3b82f6, #8b5cf6)',
            backgroundSize: '400% 400%',
            animation: 'gradientShift 15s ease-in-out infinite'
          }}
        ></div>

        {/* Floating orbs for extra depth */}
        <div className="absolute top-1/4 left-1/4 w-32 h-32 bg-blue-400/20 rounded-full blur-xl animate-bounce"></div>
        <div className="absolute top-3/4 right-1/4 w-24 h-24 bg-purple-400/20 rounded-full blur-xl animate-bounce" style={{animationDelay: '2s'}}></div>
        <div className="absolute bottom-1/4 left-1/3 w-20 h-20 bg-pink-400/20 rounded-full blur-xl animate-bounce" style={{animationDelay: '4s'}}></div>
      </div>

      {/* CSS for gradient animation */}
      <style jsx>{`
        @keyframes gradientShift {
          0% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
          100% {
            background-position: 0% 50%;
          }
        }
      `}</style>
    </div>
  );
};

export default AnimatedGradient;
