import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';

const ModelConfig = ({ onConfigChange, isOpen, onClose }) => {
  const [config, setConfig] = useState({
    model: 'claude-3-haiku-20240307',
    personality: 'helpful',
    maxTokens: 1500,
    temperature: 0.7
  });

  const [availableModels, setAvailableModels] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchAvailableModels();
  }, []);

  const fetchAvailableModels = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/models');
      const data = await response.json();
      setAvailableModels(data.availableModels);
    } catch (error) {
      console.error('Failed to fetch models:', error);
    }
  };

  const handleSave = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5001/api/configure', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(config)
      });

      if (response.ok) {
        onConfigChange(config);
        onClose();
      }
    } catch (error) {
      console.error('Failed to save configuration:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <Card className="w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <CardHeader>
          <CardTitle>Model Configuration</CardTitle>
          <p className="text-sm text-gray-600">
            Fine-tune your AI responses by adjusting model parameters
          </p>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Model Selection */}
          <div>
            <label className="block text-sm font-medium mb-2">Model</label>
            <select
              value={config.model}
              onChange={(e) => setConfig({...config, model: e.target.value})}
              className="w-full p-2 border rounded-md"
            >
              {Object.entries(availableModels).map(([key, model]) => (
                <option key={key} value={key}>
                  {model.name} - {model.description}
                </option>
              ))}
            </select>
            {availableModels[config.model] && (
              <div className="mt-2 text-xs text-gray-600">
                <p><strong>Cost:</strong> {availableModels[config.model].cost}</p>
                <p><strong>Speed:</strong> {availableModels[config.model].speed}</p>
                <p><strong>Quality:</strong> {availableModels[config.model].quality}</p>
              </div>
            )}
          </div>

          {/* Personality Selection */}
          <div>
            <label className="block text-sm font-medium mb-2">Personality</label>
            <select
              value={config.personality}
              onChange={(e) => setConfig({...config, personality: e.target.value})}
              className="w-full p-2 border rounded-md"
            >
              <option value="helpful">Helpful Assistant</option>
              <option value="professional">Professional Consultant</option>
              <option value="friendly">Friendly Companion</option>
            </select>
          </div>

          {/* Max Tokens */}
          <div>
            <label className="block text-sm font-medium mb-2">
              Max Tokens: {config.maxTokens}
            </label>
            <input
              type="range"
              min="100"
              max="2000"
              step="100"
              value={config.maxTokens}
              onChange={(e) => setConfig({...config, maxTokens: parseInt(e.target.value)})}
              className="w-full"
            />
            <div className="flex justify-between text-xs text-gray-500 mt-1">
              <span>Short (100)</span>
              <span>Medium (1000)</span>
              <span>Long (2000)</span>
            </div>
          </div>

          {/* Temperature */}
          <div>
            <label className="block text-sm font-medium mb-2">
              Creativity: {config.temperature}
            </label>
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={config.temperature}
              onChange={(e) => setConfig({...config, temperature: parseFloat(e.target.value)})}
              className="w-full"
            />
            <div className="flex justify-between text-xs text-gray-500 mt-1">
              <span>Deterministic (0.0)</span>
              <span>Balanced (0.5)</span>
              <span>Creative (1.0)</span>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex justify-end space-x-2 pt-4">
            <Button variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button onClick={handleSave} disabled={loading}>
              {loading ? 'Saving...' : 'Save Configuration'}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default ModelConfig;
