import React, { useState, useRef, useEffect } from 'react';
import { Send, Loader2, User, Bot } from 'lucide-react';
import { Button } from './components/ui/button';
import { Card, CardContent } from './components/ui/card';
import { Textarea } from './components/ui/textarea';

function App() {
  const [messages, setMessages] = useState([]);
  const [currentInput, setCurrentInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [pendingAnswers, setPendingAnswers] = useState({});
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Smart detection logic
  const needsClarification = (prompt) => {
    // Patterns that need clarification
    const clarificationPatterns = [
      /should i/i,
      /help me decide/i,
      /what should i do/i,
      /advice/i,
      /recommend/i,
      /choose/i,
      /better/i,
      /best/i,
      /worth it/i,
      /good idea/i,
      /bad idea/i,
      /pros and cons/i,
      /compare/i,
      /which/i,
      /what do you think/i,
      /opinion/i
    ];

    // Patterns that don't need clarification (factual questions)
    const factualPatterns = [
      /^what is/i,
      /^how to/i,
      /^define/i,
      /^explain/i,
      /^tell me about/i,
      /^what are/i,
      /^how does/i,
      /^when did/i,
      /^where is/i,
      /^who is/i,
      /^why does/i,
      /^calculate/i,
      /^convert/i,
      /^formula/i,
      /^definition/i,
      /^meaning/i
    ];

    // Check if it's a factual question first
    const isFactual = factualPatterns.some(pattern => pattern.test(prompt));
    if (isFactual) return false;

    // Check if it needs clarification
    return clarificationPatterns.some(pattern => pattern.test(prompt));
  };

  const generateClarifyingQuestions = (prompt) => {
    const lowerPrompt = prompt.toLowerCase();

    if (lowerPrompt.includes('move') || lowerPrompt.includes('relocate')) {
      return [
        "What's your primary motivation for considering this move?",
        "What's your budget range for housing?",
        "What are your main priorities (career, family, lifestyle)?"
      ];
    }

    if (lowerPrompt.includes('career') || lowerPrompt.includes('job')) {
      return [
        "What's your current field or industry?",
        "What's your experience level?",
        "What are your main career goals?"
      ];
    }

    if (lowerPrompt.includes('buy') || lowerPrompt.includes('purchase')) {
      return [
        "What's your budget range?",
        "What are your main requirements?",
        "What's your timeline for this purchase?"
      ];
    }

    if (lowerPrompt.includes('relationship') || lowerPrompt.includes('dating')) {
      return [
        "What's your current relationship status?",
        "What are you looking for in a relationship?",
        "What challenges are you facing?"
      ];
    }

    if (lowerPrompt.includes('health') || lowerPrompt.includes('medical')) {
      return [
        "What's your current health situation?",
        "Are you taking any medications?",
        "What are your main health concerns?"
      ];
    }

    // Default questions
    return [
      "Can you provide more context about your situation?",
      "What are your main priorities or constraints?",
      "What specific outcome are you hoping for?"
    ];
  };

  const handleSendMessage = async () => {
    if (!currentInput.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: currentInput.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setCurrentInput('');
    setIsLoading(true);

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      if (needsClarification(userMessage.content)) {
        const questions = generateClarifyingQuestions(userMessage.content);
        const clarifyingMessage = {
          id: Date.now() + 1,
          type: 'assistant-clarifying',
          content: "I'd like to understand your situation better to give you a personalized answer. Could you help me with a few questions?",
          questions: questions,
          timestamp: new Date()
        };

        setMessages(prev => [...prev, clarifyingMessage]);
        setPendingAnswers({});
      } else {
        // Skip to direct answer
        const answerMessage = {
          id: Date.now() + 1,
          type: 'assistant-answer',
          content: "This appears to be a factual question. I'll provide a direct answer based on available information. [This would be generated by the LLM API.]",
          timestamp: new Date()
        };

        setMessages(prev => [...prev, answerMessage]);
      }
    } catch (error) {
      console.error('Error processing message:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAnswerChange = (questionIndex, value) => {
    setPendingAnswers(prev => ({
      ...prev,
      [questionIndex]: value
    }));
  };

  const handleSubmitAnswers = async () => {
    setIsLoading(true);

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      const answerMessage = {
        id: Date.now(),
        type: 'assistant-answer',
        content: `Based on your responses, here's a personalized answer to your question. [This would be generated by the LLM API with your clarifications: ${JSON.stringify(pendingAnswers)}]`,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, answerMessage]);
      setPendingAnswers({});
    } catch (error) {
      console.error('Error generating answer:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSkipQuestions = async () => {
    setIsLoading(true);

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      const answerMessage = {
        id: Date.now(),
        type: 'assistant-answer',
        content: "Here's a general answer to your question. [This would be generated by the LLM API without clarifications.]",
        timestamp: new Date()
      };

      setMessages(prev => [...prev, answerMessage]);
      setPendingAnswers({});
    } catch (error) {
      console.error('Error generating answer:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const renderMessage = (message) => {
    if (message.type === 'user') {
      return (
        <div key={message.id} className="flex justify-end mb-4">
          <div className="flex items-start space-x-2 max-w-[80%]">
            <div className="bg-blue-500 text-white rounded-lg px-4 py-2 shadow-sm">
              <p className="text-sm">{message.content}</p>
            </div>
            <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center flex-shrink-0">
              <User className="w-4 h-4 text-white" />
            </div>
          </div>
        </div>
      );
    }

    if (message.type === 'assistant-clarifying') {
      return (
        <div key={message.id} className="flex justify-start mb-4">
          <div className="flex items-start space-x-2 max-w-[80%]">
            <div className="w-8 h-8 bg-gray-500 rounded-full flex items-center justify-center flex-shrink-0">
              <Bot className="w-4 h-4 text-white" />
            </div>
            <Card className="bg-gray-50 border-gray-200">
              <CardContent className="p-4">
                <p className="text-sm text-gray-700 mb-4">{message.content}</p>

                <div className="space-y-3">
                  {message.questions.map((question, index) => (
                    <div key={index} className="space-y-1">
                      <label className="text-sm font-medium text-gray-600">
                        {index + 1}. {question}
                      </label>
                      <Textarea
                        value={pendingAnswers[index] || ''}
                        onChange={(e) => handleAnswerChange(index, e.target.value)}
                        placeholder="Your answer..."
                        className="min-h-[60px] resize-none text-sm"
                      />
                    </div>
                  ))}
                </div>

                <div className="flex gap-2 mt-4">
                  <Button
                    onClick={handleSubmitAnswers}
                    disabled={isLoading}
                    className="bg-blue-500 hover:bg-blue-600 text-white"
                    size="sm"
                  >
                    {isLoading ? (
                      <Loader2 className="w-4 h-4 animate-spin mr-2" />
                    ) : null}
                    Submit Answers
                  </Button>

                  <Button
                    onClick={handleSkipQuestions}
                    disabled={isLoading}
                    variant="outline"
                    size="sm"
                  >
                    Skip & Continue
                  </Button>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      );
    }

    if (message.type === 'assistant-answer') {
      return (
        <div key={message.id} className="flex justify-start mb-4">
          <div className="flex items-start space-x-2 max-w-[80%]">
            <div className="w-8 h-8 bg-gray-500 rounded-full flex items-center justify-center flex-shrink-0">
              <Bot className="w-4 h-4 text-white" />
            </div>
            <Card className="bg-white border-gray-200">
              <CardContent className="p-4">
                <p className="text-sm text-gray-700 whitespace-pre-wrap">{message.content}</p>
              </CardContent>
            </Card>
          </div>
        </div>
      );
    }

    return null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 dark:from-slate-900 dark:via-slate-800 dark:to-slate-900">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          {/* Header */}
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Intent Clarifier</h1>
            <p className="text-gray-600 dark:text-gray-400">Get better responses through natural conversation</p>
          </div>

          {/* Chat Container */}
          <Card className="h-[600px] flex flex-col">
            <CardContent className="flex-1 flex flex-col p-0">
              {/* Messages Area */}
              <div className="flex-1 overflow-y-auto p-6 space-y-4">
                {messages.length === 0 ? (
                  <div className="text-center text-gray-500 dark:text-gray-400 mt-8">
                    <Bot className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                    <p>Start a conversation by asking me anything!</p>
                    <p className="text-sm mt-2">Try: "Should I move to New York?" or "What is React?"</p>
                  </div>
                ) : (
                  messages.map(renderMessage)
                )}

                {isLoading && (
                  <div className="flex justify-start mb-4">
                    <div className="flex items-start space-x-2">
                      <div className="w-8 h-8 bg-gray-500 rounded-full flex items-center justify-center flex-shrink-0">
                        <Bot className="w-4 h-4 text-white" />
                      </div>
                      <Card className="bg-gray-50 border-gray-200">
                        <CardContent className="p-4">
                          <div className="flex items-center space-x-2">
                            <Loader2 className="w-4 h-4 animate-spin text-gray-500" />
                            <span className="text-sm text-gray-500">Thinking...</span>
                          </div>
                        </CardContent>
                      </Card>
                    </div>
                  </div>
                )}

                <div ref={messagesEndRef} />
              </div>

              {/* Input Area */}
              <div className="border-t border-gray-200 dark:border-gray-700 p-4">
                <div className="flex space-x-2">
                  <Textarea
                    value={currentInput}
                    onChange={(e) => setCurrentInput(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Ask me anything... (e.g., 'Should I move to New York?')"
                    className="flex-1 min-h-[60px] resize-none"
                    disabled={isLoading}
                  />
                  <Button
                    onClick={handleSendMessage}
                    disabled={!currentInput.trim() || isLoading}
                    className="bg-blue-500 hover:bg-blue-600 text-white px-6"
                  >
                    {isLoading ? (
                      <Loader2 className="w-4 h-4 animate-spin" />
                    ) : (
                      <Send className="w-4 h-4" />
                    )}
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default App;
